        {
            currentSection += 2;
        }
        else
        {
            currentSection++;
        }

        StateHasChanged();
    }

    private void HandlePreviousSectionRequested()
    {
        // if a staff member is being registered, skip positions section
        if (Type == RegistrationType.StaffAdmin && currentSection == 4)
        {
